$(window).on('load', function () {
  var a = $('#bar-chart');
  new Chart(a, {
    type: 'horizontalBar',
    options: {
      elements: {
        rectangle: {
          borderWidth: 2,
          borderColor: 'rgb(0, 255, 0)',
          borderSkipped: 'left',
        },
      },
      responsive: !0,
      maintainAspectRatio: !1,
      responsiveAnimationDuration: 500,
      legend: {
        position: 'top',
      },
      scales: {
        xAxes: [
          {
            display: !0,
            gridLines: {
              color: '#f3f3f3',
              drawTicks: !1,
            },
            scaleLabel: {
              display: !0,
            },
          },
        ],
        yAxes: [
          {
            display: !0,
            gridLines: {
              color: '#f3f3f3',
              drawTicks: !1,
            },
            scaleLabel: {
              display: !0,
            },
          },
        ],
      },
      title: {
        display: !1,
        text: 'Chart.js Horizontal Bar Chart',
      },
    },
    data: {
      labels: ['국민은행  ', '주택도시보증공사  ', '스마트저축은행  ', '아톤모빌리티  ', '에듀택스  ', '피알앤디컴퍼니  ', '애플캐피탈  ', '오픈그룹  ', '법틀  ', '오케이앤캐시대부중개  '],
      datasets: [
        {
          label: '당월',
          data: [5145, 4160, 4032, 3604, 3201, 3102, 2304, 2409, 2021, 1005],
          backgroundColor: '#16D39A',
          hoverBackgroundColor: 'rgba(22,211,154,.9)',
          borderColor: 'transparent',
        },
        {
          label: '전월',
          data: [4000, 3000, 3000, 3000, 2500, 2500, 2000, 2000, 1000, 500],
          backgroundColor: '#F98E76',
          hoverBackgroundColor: 'rgba(249,142,118,.9)',
          borderColor: 'transparent',
        },
      ],
    },
  });
});
